import PropTypes from 'prop-types';

const MenuItem = ({ name, description, imageUrl, price }) => {
  return (
    <div
      style={{
        marginBottom: '20px',
        border: '1px solid #ccc',
        padding: '10px',
        borderRadius: '5px',
        maxWidth: '400px',
        marginLeft: 'auto',
        marginRight: 'auto',
      }}
    >
      <img src={imageUrl} alt={name} style={{ height: '400px' }} />
      <h2>{name}</h2>
      <p>{description}</p>
      <p>Price: ${price.individual.toFixed(2)}</p>
      <p>Price of Small ${price.Small.toFixed(2)}</p>
    </div>
  );
};

MenuItem.propTypes = {
  name: PropTypes.string.isRequired,
  description: PropTypes.string.isRequired,
  imageUrl: PropTypes.string.isRequired,
  price: PropTypes.shape({
    //shape is to access the nested object
    individual: PropTypes.number.isRequired,
  }).isRequired,
};

export default MenuItem;
