import PropTypes from 'prop-types';

const MenuItem = ({ name, description, imageUrl, price }) => {
  return (
    <div
      style={{
        marginBottom: '20px',
        border: '1px solid #ccc',
        padding: '10px',
        borderRadius: '5px',
        maxWidth: '400px',
        marginLeft: 'auto',
        marginRight: 'auto',
      }}
    >
      <img
        src={imageUrl}
        alt={name}
        style={{
          height: '400px',
          width: '100%',
          objectFit: 'cover',
        }}
      />
      <h2 style={{ fontSize: '24px', fontWeight: 'bold' }}>{name}</h2>
      <p
        style={{
          fontSize: '16px',
          color: '#666',
        }}
      >
        {description}
      </p>
      <p style={{ fontSize: '18px', color: '#333' }}>
        Price: ${price.individual.toFixed(2)}
      </p>
      <p style={{ fontSize: '18px', color: '#333' }}>
        Price of Small ${price.Small.toFixed(2)}
      </p>
    </div>
  );
};

MenuItem.propTypes = {
  name: PropTypes.string.isRequired,
  description: PropTypes.string.isRequired,
  imageUrl: PropTypes.string.isRequired,
  price: PropTypes.shape({
    //shape is to access the nested object
    individual: PropTypes.number.isRequired,
  }).isRequired,
};

export default MenuItem;
