import PropTypes from 'prop-types';

const getClassNameForAvailability = availability => {
  switch (availability) {
    case 'Available':
      return 'available';
    case 'Few Left':
      return 'few left';
    case 'Not Available':
      return 'not available';
    case 'Seasonal':
      return 'seasonal';
    default:
      return '';
  }
};

