const MenuItem = ({ name, description, imageUrl, price, availability }) => {
  const availabilityClass = getClassNameForAvailability(availability);
  return (
    <div className={availabilityClass}>
      <img src={imageUrl} alt={name} image="menuItemImage" />
      <h2>{name}</h2>
      <p>{description}</p>
      <p>Price: ${price.individual.toFixed(2)}</p>
      <p>Price of Small ${price.Small.toFixed(2)}</p>
    </div>
  );
};

